import styled from 'styled-components'

export const Container = styled.div`
    background-color: #17181f;
    color: #797a81;
    min-height: 100vh;
    font-family: Verdana;
`;

export const Area = styled.div`
    margin: auto;
    max-width: 980px;
    padding:10px;
`;

export const Header = styled.div`
    .nav-list{
        display: flex;
        list-style: none;
    }

    .nav-list li {
        display: flex;
        letter-spacing: 3px;
        margin-left: 100px;
        color:#fff;
      }
`;

export const lista = styled.h1`
    display: flex;
      color: #fff;
      margin-top: 100px;
      
`;

export const modal = styled.button`
    display: flex;
    padding: 10px;
    border-radius: 15px;
    margin-bottom: 10px;
    align-items: center;
    background: transparent;
    font-size: 18px;
    flex:1;
`;
