import React from "react";
import "./Modal.css";
import {AddArea} from '../index'
import {handleAddText, list, ListItem} from ".../App"

function Modal({ setOpenModal }) {
  return (
    <div className="modalBackground">
      <div className="modalContainer">
        <div className="titleCloseBtn">
          <button
            onClick={() => {
              setOpenModal(false);
            }}
          >
            X
          </button>
        </div>
        <div className="title">
          <h1>Adicionar Novo</h1>
        </div>

        <AddArea onEnter = {handleAddText} />

        {list.map((item, index)=>(
          <ListItem key={index} item={item} />
          
        ))}

        <div className="footer">
          <button
            onClick={() => {
              setOpenModal(false);
            }}
            id="cancelBtn"
          >
            Cancelar
          </button>
          <button>+ Adicionar</button>
        </div>
      </div>
    </div>
  );
}

export default Modal;
