import styled from 'styled-components'

export const Container = styled.div`
    background-color: white;
    color: #797a81;
    min-height: 100vh;
    font-family: Verdana;
`;

export const Area = styled.div`
    margin: auto;
    max-width: 980px;
    padding:10px;
`;

export const Header = styled.div`
    .nav-list{
        display: flex;
        list-style: none;
        align-items: center;
        background: black;
        height: 7vh;
    }

    .nav-list li {
        display: flex;
        letter-spacing: 3px;
        margin-left: 10%;
        color:#fff;
        cursor: pointer;
      }


`;

export const lista = styled.h1`
    display: flex;
    color: #fff;
    margin-top: 20px;
      
`;

export const modal = styled.button`
    display: block;
    align-items: center;
    background: transparent;
    font-size: 20px;
    flex:1;
`;
